async function loadAlert(e,t,l=(()=>{}),o=(()=>{})){let a=$(await $.get("/components/alert.php"));a.find(".title").html(e),a.find(".message").html(t),a.find("#popup-ok").click(()=>{a.addClass("close"),setTimeout(()=>{a.remove()},500),l()}),a.find("#popup-cancel").click(()=>{a.addClass("close"),setTimeout(()=>{a.remove()},500),o()}),$("body").append(a)}